INSERT INTO `products` VALUES ('Core','Shared components used by Firefox and other Mozilla software, including handling of Web content (Gecko, HTML, CSS, layout, scripts, images, networking, etc.).',10000,1,0,'---',1,3,1,1,0),('mozilla.org','The administration of the Mozilla Project and its servers (Website content bugs will belong in the www.mozilla.org product)',50,1,0,'---',10,5,1,1,0),('Tech Evangelism','For reporting web pages that need to be upgraded to support web standards and Gecko-based browsers. And for reporting add-ons that exhibit common problems that make Firefox run sub-optimally. (<a href=\"http://www.mozilla.org/projects/tech-evangelism/\">more info</a>)',25,1,0,'---',15,5,1,1,0),('Bugzilla','The Bugzilla bug-tracking system (<a\r\nhref=\"http://www.bugzilla.org\">more info</a>)<br>\r\n<b>note:</b> This is <b>not</b> the place to request configuration, permission, or account changes to this installation of Bugzilla (bugzilla.mozilla.org). File such changes under the appropriate component in the <a\r\nhref=\"enter_bug.cgi?product=bugzilla.mozilla.org\"><b>bugzilla.mozilla.org</b></a> product.',1000,1,0,'---',19,4,1,1,0),('Firefox','The Mozilla Foundation\'s next-generation web browser (<a href=\"http://www.mozilla.com/firefox/\">more info</a>)',1000,1,0,'---',21,2,1,1,0),('Thunderbird','The Mozilla Foundation\'s next-generation email client (<a href=\"http://www.mozilla.org/products/thunderbird/\">more info</a>)',1000,1,0,'---',23,2,1,1,0),('SeaMonkey','This product covers SeaMonkey, the further developed code of the former Mozilla 1.x application suite, including web browser, e-mail and newsgroup client, and HTML composer. (<a href=\"http://www.seamonkey-project.org/\">more info</a>)',10000,1,0,'---',25,2,1,1,0),('Testing','For bugs related to automated testing of Mozilla client code (Firefox, Thunderbird, Fennec, Gecko, etc)',25,1,0,'---',47,3,1,1,0),('Core Graveyard','Old, retired Core components',1000,1,100,'---',49,6,0,1,0),('MailNews Core','Mail and news components common to Thunderbird and SeaMonkey',1000,1,0,'---',50,3,1,1,0),('Mozilla QA','For bugs and requests related to management of <a href=\"http://quality.mozilla.org/\">Mozilla QA</a> related projects.',100,1,0,'---',64,5,1,1,0),('Mozilla Reps','More info about MozillaReps can be found on http://wiki.mozilla.org/ReMo',1,100,0,'---',68,5,1,1,0),('bugzilla.mozilla.org','For issues relating to the bugzilla.mozilla.org website, also known as BMO. More info: <a href=\"https://wiki.mozilla.org/BMO\">BMO</a> wiki pages.',100,1,0,'---',70,5,1,1,0),('www.mozilla.org','For issues with content on www.mozilla.org http servers (but not the functioning of the servers) (<a href=\"https://wiki.mozilla.org/Websites/Mozilla.org\">more info</a>)',0,1,0,'---',93,5,1,1,0);
